/**
 * Interface for data providers
 */
export interface DataProvider {
    name: string;
    initialize(): Promise<void>;
    fetchData(query: any): Promise<any>;
}
/**
 * Interface for sentiment analysis providers
 */
export interface SentimentProvider extends DataProvider {
    analyzeSentiment(data: any): Promise<{
        score: number;
        magnitude: number;
        classification: string;
        [key: string]: any;
    }>;
}
